            if (parentLayout != null && parentLayout.isSheet()) {
                adjustPanLayoutHelper.setResizableView((FrameLayout) parentLayout.getView().getParent().getParent().getParent().getParent());
            }
            adjustPanLayoutHelper.onAttach();
